function toTitle(id) {
  return id.split('_').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' ');
}

function createRecipeFigure(item) {
  const fig = document.createElement('figure');
  fig.className = 'recipe-item';
  fig.id = item.id;
  fig.onclick = () => openModal(fig);

  const wrapper = document.createElement('div');
  wrapper.className = 'wrapper';

  const view = document.createElement('img');
  view.className = 'view';
  view.src = `decorations/${item.id}.webp`;
  view.alt = toTitle(item.id);

  const recipeWrapper = document.createElement('div');
  recipeWrapper.className = 'recipe-wrapper';

  const recipe = document.createElement('img');
  recipe.className = 'recipe';
  recipe.src = `recipes/${item.id}.webp`;
  recipe.alt = `${toTitle(item.id)} recipe`;

  recipeWrapper.appendChild(recipe);
  wrapper.appendChild(view);
  wrapper.appendChild(recipeWrapper);

  const caption = document.createElement('figcaption');
  const h3 = document.createElement('h3');
  h3.textContent = toTitle(item.id);
  const a = document.createElement('a');
  a.href = item.url.startsWith('http') ? item.url : `https://${item.url}`;
  a.target = '_blank';
  a.rel = 'noopener noreferrer';
  a.textContent = item.author;
  caption.appendChild(h3);
  caption.appendChild(a);

  fig.appendChild(wrapper);
  fig.appendChild(caption);

  return fig;
}

function renderRecipes() {
  const list = document.querySelector('.recipe-list');
  if (!list) return;
  list.innerHTML = '';
  items.forEach(item => list.appendChild(createRecipeFigure(item)));
}

document.addEventListener('DOMContentLoaded', renderRecipes);

function openModal(fig) {
    const modal = document.getElementById('imageModal');
    const modalImage = document.getElementById('modalImage');
    const modalRecipe = document.getElementById('modalRecipe');

    // modalImage.src = image.src; // Use the same image source
    modalImage.src = "decorations/" + fig.id + ".webp";
    modalRecipe.src = "recipes/" + fig.id + ".webp"
    modal.style.display = 'grid';
}

// Close the modal
function closeModal() {
    const modal = document.getElementById('imageModal');
    modal.style.display = 'none';
}